import breakingUpIntoPartsId from "./breakingUpIntoPartsId";

export default function findActiveEvent(allEvent, id, eventOrOtherArray) {

    console.log('@@@@@ year, fromTime, monthAndDate',allEvent, id, eventOrOtherArray);

    const {year, fromTime, monthAndDate, _id} = breakingUpIntoPartsId(id);


    return allEvent.filter((item) => {
        console.log('@@@@@ item._id === _id', item._id === _id,item._id, _id );

        return eventOrOtherArray ?
            (item.year === +year &&
            item.monthAndDate === monthAndDate &&
            item.from === fromTime)
        :
        (!(item._id === _id))
    });

}
